INSERT INTO `cscart_page_descriptions` VALUES (1,'en','Our team','<p>\n\n  Мы рады познакомить вас с сотрудниками нашей компании. Это именно те люди, которые каждый день с любовью подбирают ассортимент интернет-магазина, консультируют вас на этапе выбора товара и общаются с вами при покупке. Это те, кто оформляет ваш заказ и заботится о надежной доставке.\n\n</p>\n\n<p>\n\n	<br>\n\n   Мы гордимся работой каждого!\n\n</p>','','','',''),(2,'en','About our company','<div class=\"ty-list-container\">\n\n	<div class=\"ty-column5\">\n\n		<div class=\"ty-company__item\">\n\n			<div class=\"ty-list-text\">\n\n				<span class=\"ty-blue-light ty-header\"><br>\n\n				<span class=\"ty-big\">2006</span></span>The year of company foundation\n\n			</div>\n\n		</div>\n\n	</div>\n\n	<div class=\"ty-column5\">\n\n		<div class=\"ty-company__item\">\n\n			<div class=\"ty-list-text\">\n\n				<span class=\"ty-blue-light ty-header\">more than<br>\n\n				<span class=\"ty-big\">150 000</span></span>goods we have sold\n\n			</div>\n\n		</div>\n\n	</div>\n\n	<div class=\"ty-column5\">\n\n		<div class=\"ty-company__item\">\n\n			<div class=\"ty-list-text\">\n\n				<span class=\"ty-blue-light ty-header\"><br>\n\n				<span class=\"ty-big\">72%</span></span>of clients do repeat purchases in our store\n\n			</div>\n\n		</div>\n\n	</div>\n\n	<div class=\"ty-column5\">\n\n		<div class=\"ty-company__item\">\n\n			<div class=\"ty-list-text\">\n\n				<span class=\"ty-blue-light ty-header\"><br>\n\n				<span class=\"ty-big\">4,2</span></span>our rating at the <a href=\"http://market.yandex.ru/\" class=\"ty-blue-light ty-underline\">Yandex Market</a>\n\n			</div>\n\n		</div>\n\n	</div>\n\n	<div class=\"ty-column5\">\n\n		<div class=\"ty-company__item\">\n\n			<div class=\"ty-list-text\">\n\n				<span class=\"ty-blue-light ty-header\">more than<br>\n\n				<span class=\"ty-big\">25000</span></span>clients\n\n			</div>\n\n		</div>\n\n	</div>\n\n</div>\n\n<div class=\"ty-promo-container\" style=\"margin: 55p